import React, { Component } from "react";
import ContentList from "../component/contentList/ContentList";
import axios from "axios"

export default class Search extends Component {
  state = {
    keyword: null,
    contents: []
  };

  handleChange = e => {
    this.setState({
      keyword: e.target.value
    });
    e.preventDefault();
  };

  handleSubmit = e => {
    this.fetchSearch(this.state.keyword);
    e.preventDefault();
  };

  fetchSearch = keyword => {
    let maxResults = 30;
    let token = 'AIzaSyC-v1sIG2Wn3YnoD_7_bBS4zPDceDLKmLY';

    try {
        let result = axios.get('https://www.googleapis.com/youtube/v3/search?q='+keyword+'&part=snippet&key='+token+'&maxResults='+maxResults)
    } catch{}
 };

  render() {
    const { keyword, contents } = this.state;
    const { handleChange, handleSubmit } = this;
    return (
      <div>
        <div>
          <form onSubmit={handleSubmit}>
            <input type="text" value={keyword} onChange={handleChange} />
          </form>
        </div>
        {keyword}
        <div className="content">
          <ContentList contents={contents} />
        </div>
      </div>
    );
  }
}
